body.script:
    //
    
    // 1. Language Logic (George Bahgat Edition)
    const html = document.documentElement;
    const langBtnTxt = document.getElementById('lang-switcher');
    
    function switchLanguage() {
        if(html.getAttribute('dir') === 'rtl') {
            html.setAttribute('dir', 'ltr');
            html.setAttribute('lang', 'en');
            localStorage.setItem('lyn-lang', 'en');
        } else {
            html.setAttribute('dir', 'rtl');
            html.setAttribute('lang', 'ar');
            localStorage.setItem('lyn-lang', 'ar');
        }
    }

    // 2. Theme Logic
    function toggleTheme() {
        const body = document.body;
        const current = body.getAttribute('data-theme');
        const next = current === 'dark' ? 'light' : 'dark';
        body.setAttribute('data-theme', next);
        localStorage.setItem('lyn-theme', next);
    }

    // 3. Real Interaction System (Like)
    function handleLike(btn, postId) {
        const countSpan = btn.querySelector('.count');
        const icon = btn.querySelector('i');
        
        let globalLikes = JSON.parse(localStorage.getItem('lyn-global-likes') || '{}');
        let userLikes = JSON.parse(localStorage.getItem('lyn-user-likes') || '{}');
        
        let currentLikes = globalLikes[postId] || 0;

        if(userLikes[postId]) {
            delete userLikes[postId];
            currentLikes = Math.max(0, currentLikes - 1);
            icon.classList.replace('fa-solid', 'fa-regular');
            btn.classList.remove('liked');
        } else {
            userLikes[postId] = true;
            currentLikes++;
            icon.classList.replace('fa-regular', 'fa-solid');
            btn.classList.add('liked');
        }
        
        globalLikes[postId] = currentLikes;
        localStorage.setItem('lyn-global-likes', JSON.stringify(globalLikes));
        localStorage.setItem('lyn-user-likes', JSON.stringify(userLikes));
        countSpan.textContent = currentLikes.toLocaleString();
    }

    // 3. Share Logic (Native Web Share)
    function handleShare(btn) {
        const title = btn.getAttribute('data-title');
        const url = btn.getAttribute('data-url');

        if (navigator.share) {
            navigator.share({ title: title, url: url })
            .catch(console.error);
        } else {
            // Fallback: Copy link and show toast
            navigator.clipboard.writeText(url).then(() => {
                const toast = document.createElement('div');
                toast.className = 'share-toast';
                toast.textContent = 'Link copied to clipboard!';
                document.body.appendChild(toast);
                setTimeout(() => toast.remove(), 3000);
            });
        }
    }

    // 4. Interaction System (Starts at 0)
    function initStats() {
        let globalViews = JSON.parse(localStorage.getItem('lyn-global-views') || '{}');
        let globalLikes = JSON.parse(localStorage.getItem('lyn-global-likes') || '{}');
        let userLikes = JSON.parse(localStorage.getItem('lyn-user-likes') || '{}');

        document.querySelectorAll('.card-social-stats').forEach(wrapper => {
            const viewEl = wrapper.querySelector('.v-num');
            const likeBtn = wrapper.querySelector('.like-btn');
            const likeCount = likeBtn.querySelector('.count');
            
            // Get ID from Like Button onclick
            const onclick = likeBtn.getAttribute('onclick') || '';
            const match = onclick.match(/handleLike\(this,\s*["']?(\d+)["']?\)/);
            
            if(match) {
                const postId = match[1];

                // 1. Handle Views (Increment on load if not seen in this session)
                if(!window['seen_' + postId]) {
                    globalViews[postId] = (globalViews[postId] || 0) + 1;
                    localStorage.setItem('lyn-global-views', JSON.stringify(globalViews));
                    window['seen_' + postId] = true;
                }
                
                // Display Stats
                if(viewEl) viewEl.textContent = (globalViews[postId] || 0).toLocaleString();
                if(likeCount) likeCount.textContent = (globalLikes[postId] || 0).toLocaleString();
                
                // Mark as Liked if user did it before
                if(userLikes[postId]) {
                    likeBtn.classList.add('liked');
                    likeBtn.querySelector('i').classList.replace('fa-regular', 'fa-solid');
                }
            }
        });
    }

    document.addEventListener('DOMContentLoaded', initStats);

    // 4. Reveal Animations on Scroll
    const observer = new IntersectionObserver((entries) => {
        entries.forEach(entry => {
            if(entry.isIntersecting) entry.target.classList.add('active');
        });
    }, { threshold: 0.1 });

    document.querySelectorAll('.reveal').forEach(el => observer.observe(el));

    // 5. Accessibility Fix for Blogger Cookie Link
    function fixA11y() {
        const cookieLinks = document.querySelectorAll('a[href*="blogspot-cookies"]');
        cookieLinks.forEach(link => {
            if (link.textContent.trim().toUpperCase() === 'LEARN MORE') {
                link.setAttribute('aria-label', 'Learn more about Blogger cookies and privacy policy');
            }
        });
    }
    setTimeout(fixA11y, 2000); // Wait for system scripts to inject

    // 6. Anti-Bypass Footer Protection (Encrypted Logic)
    (function(){
        const _0x1a = ['\x68\x74\x74\x70\x73\x3a\x2f\x2f\x67\x69\x74\x68\x75\x62\x2e\x63\x6f\x6d\x2f\x47\x65\x6f\x72\x67\x65\x42\x61\x68\x67\x61\x74', '\x6c\x79\x6e\x2d\x64\x65\x76\x2d\x6c\x69\x6e\x6b', '\x47\x65\x6f\x72\x67\x65\x20\x42\x61\x68\x67\x61\x74'];
        setInterval(function(){
            const _link = document.getElementById(_0x1a[1]);
            if (!_link || _link.href !== _0x1a[0] || _link.textContent.trim() !== _0x1a[2]) {
                window.location.href = _0x1a[0];
            }
        }, 3000);
    })();
    
    // 7. Premium Lightbox Logic
    if(document.querySelector('.post-body-text img')) {
        const lightbox = document.createElement('div');
        lightbox.className = 'lyn-lightbox';
        lightbox.innerHTML = '<img src="" alt="Lightbox"/>';
        document.body.appendChild(lightbox);

        const lightboxImg = lightbox.querySelector('img');

        document.querySelectorAll('.post-body-text img').forEach(img => {
            img.addEventListener('click', () => {
                lightboxImg.src = img.src;
                lightbox.classList.add('active');
                document.body.style.overflow = 'hidden'; 
            });
        });

        lightbox.addEventListener('click', () => {
            lightbox.classList.remove('active');
            document.body.style.overflow = '';
        });
    }


    //
